    private void loadFromFile()
    {
        try{
            BufferedReader br;
            br = new BufferedReader(new FileReader("database.txt"));
            String line;
            String locName;
            String poke;
            String how;
            int i = 0;
            int j = 0;
            DefaultListModel dlm = new DefaultListModel();
            while ((line = br.readLine()) != null) {
               if("@".equals(line.substring(0, 1)))
               {
                   locName = line.substring(1);
                   Encounter[] encData = new Encounter[185];
                   do
                   {
                    poke = br.readLine();
                    how = br.readLine();
                    if(!"+".equals(poke))
                    {
                        Encounter e = new Encounter(poke,how);
                        encData[i] = e;
                    }
                    i ++;
                   }while(!"+".equals(poke));
               Location l = new Location(locName, encData);
               locations[j] = l;
               i = 0;
               j++;
               }
            }
            br.close();

            i = 0;
            do
            {
                if(!"Evolution".equals(locations[i].toString()))
                    dlm.addElement(locations[i].toString());
                i ++;
            }while(locations[i] != null);

            locationList.setModel(dlm);
        }
        catch(FileNotFoundException ex)
        {
            final JPanel panel = new JPanel();
            JOptionPane.showMessageDialog(panel, ex.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        } catch (IOException ex) {
            final JPanel panel = new JPanel();
            JOptionPane.showMessageDialog(panel, ex.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        }
    }
    
    private void loadLocationData(String location)
    {
        String pokemon;
        Location loc = null;
        int i = 0;
        int l = 0;
        boolean done = false;
        Encounter[] encounters = null;
        DefaultListModel listModel = new DefaultListModel();
        
        do
        {
            loc = locations[l];
            l ++;
            if(loc.toString().equals(location))
                done = true;
        }while(!done);
        
        do
        {
            encounters = loc.getPokemon();
            if(encounters[i] != null)
            {
                pokemon = encounters[i].getName()+": "+encounters[i].getHow();
                listModel.addElement(pokemon);
                i ++;
            }
            else
            {
                i = 0;
            }
        }while(encounters[i] != null);
        
        resultList.setModel(listModel);
    }
    
    private void loadHunterData(String pkmn)
    {
        String res;
        Location loc;
        int i = 0;
        int l = 0;
        Encounter[] encounters;
        DefaultListModel listModel = new DefaultListModel();
        
        do
        {
            loc = locations[l];
            encounters = loc.getPokemon();
            if(encounters[i] != null)
            {
                if(encounters[i].getName().equals(pkmn))
                {
                    res = loc.toString()+": "+encounters[i].getHow();
                    listModel.addElement(res);
                }
                i ++;
            }
            else
            {
                i = 0;
                l ++;
            }
        }while(locations[l] != null);
        
        resultList.setModel(listModel);
    }        
    
